var cpTenantDomain = "numeralhq";
var cpRouterName = "book-a-demo";
//testing forn
//var formGuid = "d6e1e2a4-ed7d-4af4-89af-6f3c1e7085e9"; 
//var formGuid="6f136ca8-2f93-44e7-947a-b4af9a8c3f44";
var formGuid="81422de9-81a0-4a56-86ee-27bd95150fc5";

var lead={};
hbspt.forms.create({
  portalId: "39949294",
  formId: formGuid,
  cssClass: "v3_hero-demo-form",
  onFormReady: function () {


    // Set "Type of Business" select value:
  	const pageTypeOfBusiness = document.querySelector('.v3_page-type-of-business').innerText;
    const demoForm = document.querySelector(".v3_hero-demo-form");
    const typeOfBusinessFieldset = demoForm.querySelector("fieldset:has(.hs-type_of_business)");
    switch(pageTypeOfBusiness) {
      case "Ecommerce":
        $('select[name="type_of_business"]').val("Ecommerce").change();
        typeOfBusinessFieldset.style.display="none";
        typeOfBusinessFieldset.classList.add("hidden-fieldset");
      break;
      case "Software (SaaS)":
        $('select[name="type_of_business"]').val("Software").change();
        typeOfBusinessFieldset.style.display="none";
        typeOfBusinessFieldset.classList.add("hidden-fieldset");
      break;
      case "Partner":
        $('select[name="type_of_business"]').val("Partner").change();
        typeOfBusinessFieldset.style.display="none";
        typeOfBusinessFieldset.classList.add("hidden-fieldset");
      break;
      case "Global":
        //$('select[name="type_of_business"]').val("Other").change();
        //typeOfBusinessFieldset.style.display="none";
        //typeOfBusinessFieldset.classList.add("hidden-fieldset");
      break;
      default:
        //..
    }

    // >> Added by Ergun K.
    function getCookie(cookieName) {
      var cookies = document.cookie.split('; ');
      for (var i = 0; i < cookies.length; i++) {
          var cookie = cookies[i];
          if (cookie.indexOf(cookieName + '=') === 0) {
              return cookie.substring(cookieName.length + 1);
          }
      }
      return null;
  	}

    // >> Added by Ergun K.


    var eidValue = getCookie("eidCookie");
    if (eidValue) {
        document.querySelector('input[name="eid"]').value = eidValue;
    }



  	// >> Hide countries in Demo Form
    var countryWidget=document.querySelector(".hs_countries_operating_in")
		countryWidget.style.display="block";
    if(countryWidget) {
      const demoFormCountriesFieldset = demoForm.querySelector('.hs_countries_operating_in').parentNode;
      if(demoFormCountriesFieldset) {
        demoFormCountriesFieldset.style.display = 'none';
      }    
    }
    // << Hide countries in Demo Form


    // >> VAT page addition (May 30th 2025 by Ergun K.):

    // Make Countries checkboxes visible:
    if(pageTypeOfBusiness == 'Global') {
    	 demoForm.querySelector('.hs_countries_operating_in').parentNode.style.display = 'block'; 
    }

    // >> HS COUNTRIES SELECT

    const hsCountriesOperatingIn = document.querySelector('.hs_countries_operating_in');

		// Set/modify trigger
    const hsCountriesOperatingInLabel = document.querySelector('.hs-countries_operating_in > label');
    hsCountriesOperatingInLabel.classList.add('hs-countries_operating_in-trigger');
    const hsCountriesOperatingInTrigger = document.querySelector('.hs-countries_operating_in-trigger');
    hsCountriesOperatingInTrigger.innerHTML = `<div class="hs_countries_operating_in-label-text">Select countries</div><div class="hs_countries_operating_in-label-arrow ph ph-caret-down"></div>`;
    const hsCountriesOperatingInTriggerText = document.querySelector('.hs_countries_operating_in .hs_countries_operating_in-label-text');

		// Add "expanded" class to country checbox list when clicked to trigger
    const hsCountriesOperatingInCheckboxList = document.querySelector('.hs-countries_operating_in ul');
    hsCountriesOperatingInTrigger.addEventListener('click', () => {
      hsCountriesOperatingInTrigger.classList.add('expanded');
    	hsCountriesOperatingInCheckboxList.classList.add('expanded');
    });

		// Remove "expanded" class from country checbox list when clicked outside of trigger and list
    document.addEventListener('click', function(event) {
      if (!hsCountriesOperatingInTrigger.contains(event.target) && !hsCountriesOperatingInCheckboxList.contains(event.target)) {
      	hsCountriesOperatingInTrigger.classList.remove('expanded');
        hsCountriesOperatingInCheckboxList.classList.remove('expanded');
      }
    });

    // Create and add text label
    const hsCountriesOperatingInTopLabel = document.createElement('label');
		hsCountriesOperatingInTopLabel.innerText = "Which countries are you selling in?";
    hsCountriesOperatingIn.insertBefore(hsCountriesOperatingInTopLabel, hsCountriesOperatingInTrigger)

    const hsCountriesOperatingInCheckboxes = document.querySelectorAll('.hs_countries_operating_in .hs-form-checkbox-display input');
    let hsCountriesOperatingInSelectedCheckboxes = [];
    const updateHsCountriesOperatingInSelectedCheckboxes = () => {
      hsCountriesOperatingInTriggerText.innerText = Array.from(hsCountriesOperatingInSelectedCheckboxes).join(', ');
      if(hsCountriesOperatingInSelectedCheckboxes.length == 0) {
        hsCountriesOperatingInTriggerText.innerText = "Select countries";
        hsCountriesOperatingInLabel.style.color = 'rgba(28, 25, 23, 0.40)';
      } else {
        hsCountriesOperatingInLabel.style.color = 'var(--v3-colors--v3-gray-warm-900)';
      }
    }
    hsCountriesOperatingInCheckboxes.forEach((countriesSelectCheckbox) => {
      countriesSelectCheckbox.addEventListener('change', (checkboxItem) => {
        if(countriesSelectCheckbox.checked) {
          hsCountriesOperatingInSelectedCheckboxes.push(countriesSelectCheckbox.value);
          updateHsCountriesOperatingInSelectedCheckboxes();
        } else {
          const filteredCountriesSelectCheckedCheckboxes = hsCountriesOperatingInSelectedCheckboxes.filter(item => item !== countriesSelectCheckbox.value);
          hsCountriesOperatingInSelectedCheckboxes = filteredCountriesSelectCheckedCheckboxes;
          updateHsCountriesOperatingInSelectedCheckboxes();
        }
      });
    });

      // << HS COUNTRIES SELECT



			// >> TYPE SEARCH

				// Filter country checkboxes by input in "Select countries" field:

        const hsCountriesOperatingInTriggerInput = document.createElement('input');
        hsCountriesOperatingInTriggerInput.setAttribute('placeholder', 'Select countries');
        hsCountriesOperatingInTriggerInput.classList.add('hs-countries_operating_in-trigger-country-search')
        hsCountriesOperatingInTrigger.insertBefore(hsCountriesOperatingInTriggerInput, hsCountriesOperatingInTriggerText);

        const checkboxes = document.querySelectorAll(".hs-countries_operating_in li.hs-form-checkbox");

        // "No results" text
        const noResultsText = document.createElement('div');
        noResultsText.classList.add('no-result-text');
        noResultsText.innerText = "No results";
        noResultsText.style.display = 'none';
        hsCountriesOperatingInCheckboxList.appendChild(noResultsText)

        // Filter logic
        const filterCheckboxes = () => {
					const filter = hsCountriesOperatingInTriggerInput.value.toLowerCase();

          checkboxes.forEach((li) => {
            const country = li.querySelector("span").textContent.toLowerCase();
            li.style.display = !filter || country.includes(filter) ? "block" : "none";

            // Add/remove "visible" class to show/hide "No results" text
            if(!filter || country.includes(filter)) {
            	li.classList.add('visible')
						} else {
            	li.classList.remove('visible')
            }

          });

					// Show hide "No results" text
					const visibleCheckboxes = document.querySelectorAll(".hs-countries_operating_in li.hs-form-checkbox.visible");
					if(visibleCheckboxes.length == 0) {
          	noResultsText.style.display = 'block';
          } else {
          	noResultsText.style.display = 'none';
          }

        };

        hsCountriesOperatingInTriggerText.addEventListener("click", function () {
          hsCountriesOperatingInTriggerInput.style.display = 'block !important';
        });

				// Show trigger text on blur
        hsCountriesOperatingInTriggerInput.addEventListener("blur", function () {
        	hsCountriesOperatingInTriggerText.style.display = 'block';
        });


        // Filter on input
        hsCountriesOperatingInTriggerInput.addEventListener("input", filterCheckboxes);

        // Also filter on any keyup (including delete/backspace)
        hsCountriesOperatingInTriggerInput.addEventListener("keyup", filterCheckboxes);

        // Prevent newlines
        hsCountriesOperatingInTriggerInput.addEventListener("keypress", function (e) {
          if (e.key === "Enter") e.preventDefault();
        });

				// << TYPE SEARCH



    // << VAT page addition (May 30th 2025 by Ergun K.):



    // Check country checkboxes in Demo Form using selected countries in flow 3
    const demoFormCountries = demoForm.querySelectorAll('.hs_countries_operating_in input[type="checkbox"]');
    const selectedCountryList=getCookie('selectedCountries');
    if(selectedCountryList) {
      const selectedCountries = getCookie('selectedCountries').split(",");
    	selectedCountries.forEach((selectedCountry) => {
      demoFormCountries.forEach((demoFormCountry) => {
        if(demoFormCountry.value == selectedCountry) {
          $("#"+demoFormCountry.id).prop('checked', true).change();
        }
      });
    	});
    }

  }, // On Hubspot form ready end

