  document.addEventListener("DOMContentLoaded", () => {
    const form = document.getElementById("wf-form-Signup-Form");
    const emailField = document.getElementById("signup-email-field");

    const passwordField = document.getElementById("signup-pw-field");
    const submitButton = form.querySelector(".signup-form-btn");

    const pwWrapper = passwordField.closest(".field-wrap");
    const minCharMsg = document.getElementById("min-8-char");
    const numMsg = document.getElementById("least-1-num");

    let step = 1;

    // Init: hide password & warnings
    pwWrapper.style.display = "none";
    minCharMsg.style.display = "none";
    numMsg.style.display = "none";

    // Send a verification email to verify a user's email
    async function sendVerificationToken(email) {
      try {
        const response = await fetch(`https://dashboard.numeralhq.com/api/trpc/user.sendVerificationToken?batch=1`, {
          method: 'POST',
          body: JSON.stringify({ "0": { "json": { "email": email } } }),
        });

        if (!response.ok) {
          throw new Error('Failed to send verification email');
        }

        return await response.json();
      } catch (error) {
        console.error("Failed to send verification email:", error);
        throw error;
      }
    }

    submitButton.addEventListener("click", async (e) => {
      e.preventDefault();

      const email = emailField.value.trim();
      const password = passwordField.value.trim();

      if (step === 1) {
        if (!email || !email.includes("@")) {
          showModal("Please enter a valid email.");
          return;
        }

        // Lock email, show password
        emailField.disabled = true;
        emailField.style.opacity = "0.5";
        pwWrapper.style.display = "block";
        submitButton.value = "Sign up";
        step = 2;
        return;
      }

      // Step 2: validate password
      minCharMsg.style.display = "none";
      numMsg.style.display = "none";

      let valid = true;

      if (password.length < 8) {
        minCharMsg.style.display = "block";
        valid = false;
      }

      if (!/\d/.test(password)) {
        numMsg.style.display = "block";
        valid = false;
      }

      if (!valid) return;

      const urlParams = new URLSearchParams(window.location.search);
      const formData = {
        email,
        password,
        isNewUser: "true",
        utmSource: urlParams.get("utm_source") || "",
        utmMedium: urlParams.get("utm_medium") || "",
        utmCampaign: urlParams.get("utm_campaign") || "",
        utmContent: urlParams.get("utm_content") || "",
        gclid: urlParams.get("gclid") || "",
        verificationToken: "",
        redirect: "false",
        callbackUrl: "https://dashboard.numeralhq.com/dashboard",
        json: "true"
      };

      const shopToken = urlParams.get("shopToken") || "";

      try {
        const csrfRes = await fetch("https://dashboard.numeralhq.com/api/auth/csrf", {credentials: "include"});
        const { csrfToken } = await csrfRes.json();

        const response = await fetch(
          `https://dashboard.numeralhq.com/api/auth/callback/credentials?shopToken=${shopToken}`,
          {
            credentials: "include",
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ ...formData, csrfToken })
          }
        );

        const result = await response.json();

        // Add debugging to see what's actually returned
        console.log("Signup response:", response.ok, result);

        // Handle existing unverified email first
        if (result?.url?.includes("error=Email%20not%20verified")) {
          await sendVerificationToken(email);
          window.location.href = `https://dashboard.numeralhq.com/users/check-inbox?email=${email}`;
          return;
        }

        // Check if there's any other error
        if (result?.error || result?.url?.includes("error=")) {
          showError(result?.error || "Error registering your account.");
          return;
        }

        // If we get here and response is ok, assume success
        if (response.ok) {
          // Send verification email immediately after successful signup
          try {
            await sendVerificationToken(email);
          } catch (emailError) {
            // Continue even if verification email fails
            console.error("Verification email failed, but account was created");
          }

          if (window.dataLayer) {
            window.dataLayer.push({ event: "sign_up" });
          }

          showSuccess("Account created! Please check your email to verify your account.");
          setTimeout(() => {
            window.location.href = `https://dashboard.numeralhq.com/users/check-inbox?email=${email}`;
          }, 3000);
          return;
        }

        // Fallback error
        showError("Error registering your account.");

      } catch (err) {
        console.error("Signup error:", err);
        showError("Unexpected error. Please try again.");
      }
    });

    function showError(msg) {
      showModal(msg);
    }

    function showSuccess(msg) {
      showModal(msg);
    }

    function showModal(message) {
      const modal = document.getElementById("modal");
      const messageEl = document.getElementById("modal-message");
      const closeBtn = document.getElementById("modal-close");

      messageEl.textContent = message;
      modal.style.display = "block";

      closeBtn.onclick = () => {
        modal.style.display = "none";
      };

      window.onclick = (e) => {
        if (e.target === modal) modal.style.display = "none";
      };
    }

    // ✅ Google OAuth signup (redirect version)
    const googleBtn = document.getElementById('google-oauth-button-signup');
    if (googleBtn) {
      googleBtn.addEventListener('click', async function () {
        try {
          const csrfRes = await fetch('https://dashboard.numeralhq.com/api/auth/csrf', {credentials: "include"});
          const { csrfToken } = await csrfRes.json();

          const shopToken = new URLSearchParams(window.location.search).get("shopToken") || "";

          if (window.dataLayer) {
            window.dataLayer.push({ event: "sign_up" });
          }
          const oauthResponse = await fetch(`https://dashboard.numeralhq.com/api/auth/signin/google?shopToken=${shopToken}`, {
              credentials: "include",
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({ csrfToken, json: "true", callbackUrl: "https://dashboard.numeralhq.com/dashboard" })
          });
          const {url} = await oauthResponse.json()
          if (url) {
            window.location.href = url
          } else {
            throw new Error("No url was returned from Google OAuth")
          }
        } catch (err) {
          console.error("OAuth signup failed", err);
          showModal("Google signup failed. Please try again.");
        }
      });
    }
  });

