.v3_demo-hero.get-started:after {
  content: "";
  position: absolute;
  top: 0;
  left: 50%;
  width: 50%;
  height: 100%;
  background: #FDFDFC;
  z-index: -1;
  border-left: 1px solid #E3D9CB;
}

.v3_demo-hero-second.get-started .v3_hero-demo-form {
  background: transparent !important;
  border: none !important;
}


@media screen and (max-width: 991px) {

  .v3_demo-hero:after {
    display: none;
  }

}
