      const otherCountriesSelectCheckboxes = document.querySelectorAll('.v3_demo-flow_other_selling_countries-select-options .v3_demo-flow-form-checkbox input');
      let countriesSelectCheckedCheckboxes = [];
      const updatecountriesSelectCheckedCheckboxes = () => {
        otherCountriesSelectText.innerText = Array.from(countriesSelectCheckedCheckboxes).join(', ');
        if(countriesSelectCheckedCheckboxes.length == 0) {
          otherCountriesSelectText.innerText = "Please select countries that are not listed above";
          otherCountriesSelectText.style.color = 'rgba(28, 25, 23, 0.40)';
        } else {
        	otherCountriesSelectText.style.color = 'var(--v3-colors--v3-gray-warm-900)';
        }
      }
      otherCountriesSelectCheckboxes.forEach((countriesSelectCheckbox) => {
        countriesSelectCheckbox.addEventListener('change', (checkboxItem) => {
          if(countriesSelectCheckbox.checked) {
            countriesSelectCheckedCheckboxes.push(countriesSelectCheckbox.value);
            updatecountriesSelectCheckedCheckboxes();
          } else {
            const filteredCountriesSelectCheckedCheckboxes = countriesSelectCheckedCheckboxes.filter(item => item !== countriesSelectCheckbox.value);
            countriesSelectCheckedCheckboxes = filteredCountriesSelectCheckedCheckboxes;
            updatecountriesSelectCheckedCheckboxes();
          }
        });
