      const countrySelectCheckboxes = document.querySelectorAll('.v3_demo-flow-form-checkbox input:not([value="Other"])');
      let selectedCountries = [];

      countrySelectCheckboxes.forEach((countrySelectCheckbox) => {
        countrySelectCheckbox.addEventListener('change', (checkboxItem) => {

          //set selectedCountries cookie to pass country to demo embed form (this cookie will be used in the code embed at the bottom of the page):
          const setSelectedCountriesCookie = () => {
          	document.cookie = "selectedCountries=" + selectedCountries + "; path=/";
          }

          if(countrySelectCheckbox.checked) {
          	selectedCountries.push(countrySelectCheckbox.value);
            setSelectedCountriesCookie();
          } else {
          	selectedCountries = selectedCountries.filter(item => item !== countrySelectCheckbox.value);
            setSelectedCountriesCookie();
          }

          // Checked checkboxes
          const checkedCountrySelectCheckboxes = document.querySelectorAll('.v3_demo-flow-form-checkbox input:not([value="Other"]):checked');

          // If there is no checked items, disable "Continue" button:
          if(checkedCountrySelectCheckboxes.length == 0) {
            document.querySelector('.continue-button').classList.add('disabled');
          } else {
            document.querySelector('.continue-button').classList.remove('disabled');
          }




        }); // countrySelectCheckbox.addEventListener
      }); // countrySelectCheckboxes.forEach

      // << COUNTRY SELECTION

    }) // THEN
    .catch(err => {
      console.error('Error populating countries:', err);
    });

  // < Call populateCountries function


}); // << DOCUMENT LOAD

