/**
  * Populates the country checkboxes by cloning a hidden template.
  * @returns {Promise<number>} Resolves with the number of countries populated.
  */
function populateCountries() {
  return new Promise((resolve, reject) => {
    try {
      const container     = document.querySelector('.v3_demo-flow_other_selling_countries-select-options');
      const labelTemplate = container.querySelector('.label-template');

      if (!labelTemplate) {
        throw new Error('Template element not found');
      }

      // Clone-and-append for each country
      countries.forEach(country => {
        const clone = labelTemplate.cloneNode(true);
        clone.classList.remove('label-template');

        const inputEl = clone.querySelector('input[type="checkbox"]');
        const spanEl  = clone.querySelector('span.v3_demo-flow-form-checkbox-label');

        inputEl.id    = country;
        inputEl.value = country;
        spanEl.setAttribute('for', country);
        spanEl.textContent = country;

        container.appendChild(clone);
      });

      // Remove original template
      labelTemplate.remove();

      resolve(countries.length);
    } catch (err) {
      reject(err);
    }
  });
}

