.v3_demo-hero-testimonials {
  width: 100%;
  max-width: 42rem;
  margin: 4rem auto 0 auto;
}

.v3_demo-flows .swiper-slide {
  text-align: center;
  display: flex;
  flex-direction: column;
  align-items: center;
}

.v3_demo-hero-testimonials-wrapper {
  width: 120%;
  transform: translateX(-10%);
  padding: 0 12%;
  position: relative;
  z-index: -2;
  mask-image: linear-gradient(to right, transparent 0%, black 15%, black 85%, transparent 100%);
}

.v3_demo-hero-testimonials-pagination {
  display: flex;
  grid-column-gap: 0.5rem;
}

.v3_demo-hero-testimonials-pagination span.swiper-pagination-bullet {
  width: 14px;
  height: 14px;
  border-radius: 50%;
  border: 1px solid rgba(28, 25, 23, 0.8);
  background: transparent;
  margin: 0 !important;
  padding: 0 !important;
}

.v3_demo-hero-testimonials-pagination span.swiper-pagination-bullet.swiper-pagination-bullet-active {
  background: var(--v3-colors--v3-orange-700);
  border-color: var(--v3-colors--v3-orange-700);
}

/* Made pagination left aligned on desktop for Get Started page's Demo Hero testimonials */
@media screen and (min-width: 992px) {
  .v3_demo-hero-content .v3_demo-hero-testimonials-pagination {
    justify-content: flex-start;
  }
}


/* >> Mobile styles */

@media screen and (max-width: 991px) {

  .v3_demo-hero-testimonials {
  	margin: 2rem auto 0 auto;
  }

  .v3_demo-flows .v3_demo-hero-testimonials {
    margin: 4rem auto 0 auto;
  }

}

@media screen and (max-width: 767px) {

  .v3_demo-hero-testimonials-pagination span.swiper-pagination-bullet {
 	 	width: 12px;
  	height: 12px;			
  }

}

